    Projet_Global_Parameter.resGetUrlData();

    console.log(Projet_Global_Parameter.aUrlData[0]);


    // 获取预约人数
    // Projet_Global_Parameter.getBindPerson();

    // 模拟登录
    Projet_Global_Parameter.testLogin();

    // 获取用户信息
    Projet_Global_Parameter.getUserInfo();


    // 答题选择
    $(document).on('click', '.wenda dd p', function (event) {
        event.preventDefault();
        $(this).addClass('curr').siblings().removeClass('curr');

        $('.btn-submit').attr('data-type', $(this).attr('data-type'));
    });

    // 获取题目
    $(document).on('click', '.question-wrap .question-box', function (event) {
        event.preventDefault();

        // 获取登录状态
        sess_isLogin = sessionStorage.getItem('sess-isLogin');
        if (JSON.parse(sess_isLogin)) {
            let iNow = $(this).index() + 1;

            let type = $(this).attr('data-type');
            if (type == '2') {
                // fb分享本提 传 id
                Projet_Global_Parameter.fbShare(type);
            } else if (type == '1') {
                layer.msg('你已获得该奖励，赶快前往奖励背包查看吧！');
            } else {
                // 发送请求
                Projet_Global_Parameter.getQuestion(iNow);
            }

        } else {
            dialog.alertPopLogin();
        }
    });

    // 关闭题目
    $(document).on('click', '.answer-pop .answer-pop-close', function (event) {
        $('.answer-box .dati').empty();
    });

    // 答题
    $(document).on('click', '.answer-pop .btn-submit', function (event) {
        event.preventDefault();
        let answer = $(this).attr('data-type');
        let subId = $(this).attr('data-answerid');

        if (answer != '') {

            // 发送请求
            Projet_Global_Parameter.answer(subId, answer);

        } else {
            layer.msg('請選擇');
        }
    });

    // fb分享
    $(document).on('click', '.pop-error .btn_fb_share', function (event) {
        event.preventDefault();
        Projet_Global_Parameter.fbShare();
    })

    // 奖励背包
    $('.left-lantern .lantern-gift,.lantern-center .btn-bb').on('click', function (param) {
        // 获取登录状态
        sess_isLogin = sessionStorage.getItem('sess-isLogin');
        if (JSON.parse(sess_isLogin)) {
            Projet_Global_Parameter.getDrawLog();
        } else {
            dialog.alertPopLogin();
        }
    });

    // 中奖兑换码 -- 复制
    $(document).on('click', '.dl_lists .copyele', function (event) {
        event.preventDefault();
        var $getId = $(this).siblings('dd').find('span').attr('id');
        // console.log($getId);
        Projet_Global_Parameter.tapCopy($getId);
        layer.msg('複製成功！趕快打開遊戲兌換獎勵吧！');
    });

    // 点赞
    $('.btn-fb-fxt').on('click', function () {
        // 获取登录状态
        sess_isLogin = sessionStorage.getItem('sess-isLogin');
        if (JSON.parse(sess_isLogin)) {
            Projet_Global_Parameter.fbLike();
        } else {
            dialog.alertPopLogin();
        }
    });


